import React, { useState, useEffect } from "react";
import { View, SafeAreaView, FlatList} from "react-native";
import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';
import Footer from '../components/Footer'

import { ReceipeCard, HomeHeader, FocusedStatusBar } from "../components";
import { COLORS, ReceipeData } from "../constants";
import { apiKeyAnneJu, apiKeySimon, apiKeyTheo ,apiKeyMael1, apiKeySimon2, apiKey} from "../constants/api";

const Home = () => {
  const [mealData, setMealData] = useState(null);
  
  const handleSearch = (value) => {
    if (value.length === 0) {
      setReceipeData(ReceipeData);
    }

    const filteredData = ReceipeData.filter((item) =>
      item.name.toLowerCase().includes(value.toLowerCase())
    );

    if (filteredData.length === 0) {
      setReceipeData(ReceipeData);
    } else {
      setReceipeData(filteredData);
    }
  };



  const getMeal = () => {
    fetch(
      `https://api.spoonacular.com/recipes/complexSearch?apiKey=${apiKey}&query=healthy&number=1&addRecipeInformation=true&fillIngredients=true&addRecipeNutrition=true&addRecipeInformation=true`
    )
    .then(response => response.json())
    .then((data) => {
      //console.log("\n\n\n\n\n\n\n\n\n\n\nLISTES DES PLATS\n",data)
      setMealData(data.results)
    })
    .catch(() => {
      console.log("error")
    })
  };

  useEffect(() => {
    getMeal();
  }, []);

  return (
    <SafeAreaView style={{flex: 1, backgroundColor: COLORS.primary}}>
      <FocusedStatusBar backgroundColor={COLORS.primary} />
      <View style={{ flex: 1 }}>

        <View style={{ zIndex: 0 }}>
          <FlatList
            data={mealData}
            renderItem={({ item }) => <ReceipeCard data={item} />}
            keyExtractor={(item) => item.id}
            showsVerticalScrollIndicator={false}
            ListFooterComponent={<Footer/>}
            ListHeaderComponent={<HomeHeader onSearch={handleSearch} />}
          />
        </View>

        <View
          style={{
            position: "absolute",
            top: 0,
            bottom: 0,
            right: 0,
            left: 0,
            zIndex: -1,
          }}
        >
          <View
            style={{ height: 300, backgroundColor: COLORS.primary }} />
          <View style={{ flex: 1, backgroundColor: COLORS.primary }} />
        </View>

      </View>
    </SafeAreaView>
  );
};

export default Home;
