import { SafeAreaView, StatusBar, FlatList ,StyleSheet,Text, ScrollView} from "react-native";

import { FocusedStatusBar } from "../components";

import {RecetteHeader, EtapeCard} from "../components/Recette";

const Details = ({ route }) => {

  const { data } = route.params;
  //v console.log("\n\n\n\n\n\n\nTEST",JSON.stringify(data).substring(0, 31000))
  return (
    <SafeAreaView style={styles.container}>
      <FocusedStatusBar
        barStyle="dark-content"
        backgroundColor="transparent"
        translucent={true}
      />
      <FlatList
          data={data["analyzedInstructions"][0].steps}
          renderItem={({ item }) => <EtapeCard data={item}/>}
          keyExtractor={(item) => item.number}
          showsVerticalScrollIndicator={false}
          ListHeaderComponent={<RecetteHeader data={data} />}
      />
      <ScrollView>
        <Text>
          {data}
        </Text>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
    margin:30,
  },
  scrollView: {
    backgroundColor: 'transparent',
  },
  text: {
    justifyContent:"space-between",
    fontSize: 15,
  },
  image:{
    width: "100%",
    height: 250,
  }
});


export default Details;
