import { View, Image, TextInput ,StyleSheet,KeyboardAvoidingView, TouchableOpacity, Text} from 'react-native'
import React, { useEffect , useState} from 'react'
import { auth } from '../firebase'
import { useNavigation } from '@react-navigation/native'
import SignUp from '../screens/SignUp'
import logo from "../assets/images/logo.png";
import ForgotPassword from './ForgotPassword'


const LoginScreen = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const navigation = useNavigation();

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(user => {
            if(user){
                navigation.replace("Home")
            }
        })
        return unsubscribe
    },[])

    const handleLogin = () =>{
        auth
        .signInWithEmailAndPassword(email,password)
        .then(userCredentials => {
            const user = userCredentials.user;
            console.log("Login with :",user.email);
        })
        .catch(error => alert(error.message))
    }


  return (
    <KeyboardAvoidingView
    style={styles.container}
    behavior="padding"
    >
      <View  style={styles.inputcontainer}>
        <View style={styles.imgcontainer}>
            <Image 
                style={styles.imagelogo}
                source={logo}
            />
            <Text style={styles.txttittle}>Login</Text>
        </View>
        <TextInput
        placeholder="Email"
        placeholderTextColor="grey"
        value={email}
        onChangeText={text =>setEmail(text.toLowerCase())}
        style={styles.input}
        />
        <TextInput
        placeholder="Password"
        placeholderTextColor="grey"
        value={password }
        onChangeText={text => setPassword(text)}
        style={styles.input}
        secureTextEntry
        />
        
      </View>
      
      <View style={styles.buttonContainer}>
        <TouchableOpacity
        onPress={() => navigation.navigate(ForgotPassword)}
        >
            <Text >Forgotten passwords</Text>
        </TouchableOpacity>
        <TouchableOpacity
        onPress={handleLogin}
        style={styles.button}
        >
            <Text style={styles.buttonText}>Login</Text>
        </TouchableOpacity>
        <TouchableOpacity
        onPress={() => navigation.navigate(SignUp)}
        >
            <Text style={styles.underline}>Create an account</Text>
        </TouchableOpacity>
      </View>
     
    </KeyboardAvoidingView>
  )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        justifyContent:'center',
        alignItems:'center',
        backgroundColor:'#F1F1F1'
    },
    underline:{
        textDecorationLine: "underline"
    },
    inputcontainer:{
        width:'80%',
    },
    imgcontainer:{
        width:'100%',
        justifyContent:'center',
        alignItems:'center',
    },
    imagelogo:{
        width: 200,
        height: 200,
        resizeMode: "contain",
    },
    txttittle:{
        fontWeight: 'bold',
        fontSize: 28,
        padding: 5,
    },
    input:{
        backgroundColor:'white',
        paddingHorizontal: 15,
        paddingVertical:10,
        borderRadius:10,
        marginTop:'3%',
        //color: "#000000",
    },
    buttonContainer:{
        width:'60%',
        justifyContent:'center',
        alignItems:'center',
        marginTop:'5%',
    },
    button:{
       backgroundColor:'#D26767',
       width:'100%',
       padding:15,
       borderRadius:10,
       alignItems:'center',
       marginBottom:'2%',
       marginTop: '2%',
    },
    buttonOutline:{
        backgroundColor:'white',
        marginTop:5,
        borderColor:'#D26767',
        borderWidth:2,
    },
    buttonText:{
        color:'white',
        fontWeight:'700',
        fontSize:16,
    },
    buttonOutlineText:{
        color:'#D26767',
        fontWeight:'700',
        fontSize:16,
    },
})

export default LoginScreen;
